<?php
/**
 * Template with importer form
 *
 * This template can be overridden by copying it to
 *   theme-child/template-parts/importer/step-1.php
 *   theme/template-parts/importer/step-1.php
 *
 * @var array $args Arguments passed to template
 *
 * @author  8guild
 * @package Guild\Importer
 */

if ( ! defined( 'ABSPATH' ) ) {
	exit;
}

?>
<div class="wrap">

	<div class="gi-loading">
		<img src="data:image/gif;base64,">
	</div>

	<h1><?php echo esc_html( $args['instance']->page_title ); ?></h1>

	<div class="gi-info gi-success">
		<strong>
			Importing process can take 1-5 minutes. Please be patient and
			do not close this page until importing is finished.
		</strong>
	</div>


	<form method="POST" id="gi-do-import" action="<?php echo esc_url( $args['instance']->action ); ?>">
		<input type="hidden" name="action" value="guild_import">

		<?php
		wp_nonce_field( $args['instance']->nonce, $args['instance']->nonce_field );

		$variants = $args['instance']->variants;

		// check if passed a single variant to the importer
		// it makes no sense to show control when passed only one variant
		if ( count( $variants ) > 1 ) {
			// template for single variant
			$template = '
			<li class="{class}">
				<label for="{for}">
					<input type="radio" name="variant" value="{value}" id="{id}" {checked}>
					<img src="{preview}" alt="{alt}">
				</label>
				<p>{name}<br><a href="{link}" target="_blank" class="preview-link button">View Demo</a></p>
			</li>
			';

			$i = 0;
			echo '<ul class="gi-variants-preview">';
			foreach ( $variants as $variant ) {
				$is_first = ( 0 === $i );
				$key      = esc_attr( $variant['key'] );
				$id       = esc_attr( 'gi-' . $key . '-variant' );

				$r = array(
					'{class}'   => $is_first ? 'active' : 'inactive',
					'{for}'     => $id,
					'{id}'      => $id,
					'{value}'   => $key,
					'{checked}' => checked( $is_first, true, false ),
					'{preview}' => esc_url( $variant['preview'] ),
					'{alt}'     => $id,
					'{name}'    => esc_html( $variant['title'] ),
					'{link}'    => esc_url( $variant['link'] ),
				);

				echo str_replace( array_keys( $r ), array_values( $r ), $template );
				$i++;
			}
			echo '</ul>';
		} else {
			$variant = reset( $variants );
			printf( '<input type="hidden" name="variant" value="%s">', esc_attr( $variant['key'] ) );
			unset( $variant );
		}
		?>

		<div class="gi-info">

			<h3>Advanced Options</h3>

			<div class="gi-options-group">
				<label for="gi-download-attachments">
					<input type="checkbox" name="import_attachments" value="1" id="gi-download-attachments" checked>
					Import media
				</label>
			</div>

<!--			<div class="gi-options-group">-->
<!--				<label for="gi-resize-attachments">-->
<!--					<input type="checkbox" name="resize_attachments" value="0" id="gi-resize-attachments">-->
<!--					Resize media (skip time-consuming thumbnail generation)-->
<!--				</label>-->
<!--			</div>-->

			<div class="gi-options-group">
				<label for="gi-authors">Authors</label>
				<select name="authors" id="gi-authors">
					<option value="create">Import</option>
					<option value="skip">Skip</option>
				</select>
			</div>
		</div>

		<div class="gi-info">
			<h3>Revolution Slider Options</h3>

			<div class="gi-options-group">
				Custom Animations:

				<label for="gi-revslider-animations-overwrite">
					<input type="radio" name="update_animations" id="gi-revslider-animations-overwrite" value="true" checked="checked">
					overwrite
				</label>

				<label for="gi-revslider-animations-append">
					<input type="radio" name="update_animations" id="gi-revslider-animations-append" value="false">
					append
				</label>
			</div>

			<div class="gi-options-group">
				Custom Navigations:

				<label for="gi-revslider-navigations-overwrite">
					<input type="radio" name="update_navigations" id="gi-revslider-navigations-overwrite" value="true" checked="checked">
					overwrite
				</label>

				<label for="gi-revslider-navigations-append">
					<input type="radio" name="update_navigations" id="gi-revslider-navigations-append" value="false">
					append
				</label>
			</div>

			<div class="gi-options-group">
				Static Styles:

				<label for="gi-revslider-static-overwrite">
					<input type="radio" name="update_static_captions" id="gi-revslider-static-overwrite" value="true">
					overwrite
				</label>

				<label for="gi-revslider-static-append">
					<input type="radio" name="update_static_captions" id="gi-revslider-static-append" value="false">
					append
				</label>

				<label for="gi-revslider-static-none">
					<input type="radio" name="update_static_captions" id="gi-revslider-static-none" value="none" checked="checked">
					ignore
				</label>
			</div>
		</div>

		<div class="gi-submit-wrapper">
			<button type="submit" name="guild_do_import" id="gi-submit" class="gi-button" value="1">Import</button>
		</div>
		
	</form>


</div>
